import React from 'react';

const TransactionItem = ({ transaction: { text, amount, } }) => {
  return (
    <>
      <li className={amount < 0 ? 'minus' : 'plus'}>
        {text}{' '}
        <span>
          {amount < 0 ? '-' : '+'}${Math.abs(amount)}
        </span>
        <button className="delete-btn">x</button>
      </li>
    </>
  );
};

export default TransactionItem;
